(43, 1, '2018-08-15 17:02:08', '2018-08-15 17:02:08', '[insert_php]\r\n$host = \"localhost\";\r\n$username = \"root\";\r\n$password = \"\";\r\n$database = \"wordpress\";\r\n\r\n// mysql_connect($host,$username,$password) or die(\'error\');\r\n// mysql_select_db($database) or die(\'error\');\r\n\r\n$mysqli= new MySQLi($host,$username,$password,$database) or die(mysql_error());\r\nif($mysqli->connect_error){\r\n  die(\"conniection failed\");\r\n}\r\n\r\n\r\nfunction final_price($awal,$discount){\r\n  global $final_price;  \r\n  if($discount > 0){\r\n    $hargadiscount = $awal * $discount/100;\r\n    $final_price = $awal - $hargadiscount;\r\n  }else{\r\n    $final_price = $awal;\r\n  }\r\n  return $final_price;\r\n}\r\n[/insert_php]\r\n\r\n          <div class=\"custslider\">\r\n            <ul class=\"anim-slider\">\r\n              <li class=\"anim-slide\" style=\"background-image:url(\'http://localhost/project/ourboutique/wp-content/uploads/2018/07/slider-1.jpg\'); background-size: cover;\">\r\n                <img src=\"http://localhost/project/ourboutique/wp-content/uploads/2018/07/textslide-1.png\" id=\"slide-1\" class=\"slide-11\">\r\n                <img src=\"http://localhost/project/ourboutique/wp-content/uploads/2018/07/buttonslide-1.png\" id=\"slide-2\" class=\"slide-12\">\r\n              </li>\r\n            </ul>\r\n          </div>\r\n          <div class=\"listwrapper full-length padding-basic column\">\r\n            <div class=\"spccols-3\">\r\n              <form method=\"GET\" action=\"http://localhost/project/ourboutique/\">\r\n              <div class=\"control-list\">\r\n                <div class=\"control-box\">\r\n                  <div class=\"title-control nopad\">\r\n                    <table class=\"full-length\" cellspacing=\"0\" cellspacing=\"0\">\r\n                      <tr>\r\n                        <td><b class=\"size-head4\">BRAND</b> </td>\r\n                        <td>\r\n                          <div class=\"title-control-sp