case \'pre-loved\':\r\n                        $status=\"pl\";\r\n                        break;\r\n                    }\r\n                    $linkpage = \"?status=\".$_GET[\'status\'].\'&\';\r\n                    $sql=\"SELECT * FROM wp_product WHERE status=\'$status\' $sortby\";\r\n                    $input = \'<input type=\"hidden\" value=\"\'.$_GET[\'status\'].\'\" name=\"status\">\';\r\n                  }else if (isset($_GET[\'search\'])){\r\n                    $linkpage = \"?search=\".$_GET[\'search\'].\'&\';\r\n                    $sql = \"SELECT * FROM wp_product WHERE productname LIKE \'%\" .$_GET[search]. \"%\' $sortby\";\r\n                    $input = \'<input type=\"hidden\" value=\"\'.$_GET[\'search\'].\'\" name=\"search\">\';\r\n                  }else{\r\n                    $linkpage = \"?\";\r\n                    $sql= \"SELECT * FROM wp_product $sortby\";\r\n                  }\r\n                [/insert_php]\r\n\r\n            <div class=\"spccols-9\">\r\n              <div class=\"full-length column top-listview\">\r\n              <div class=\"size-std cols-8 spc-tb topleft-listview\">\r\n              [insert_php] \r\n              if (isset($_GET[\'search\'])) {\r\n                $src = $mysqli->query($sql);\r\n                $foundsearch = mysqli_num_rows($src);\r\n                echo \'We found (\'.$foundsearch.\') result for <b style=\"color: red;\">\'.$_GET[\'search\'].\'</b>\';\r\n              }else{\r\n                echo\'&nbsp;\';\r\n              }\r\n              [/insert_php]\r\n              </div>\r\n              <div class=\"cols-4 spc-tb topright-listview\">                \r\n                <form method=\"GET\" action=\"http://localhost/project/ourboutique/\">\r\n                <table class=\"full-length size-std\">\r\n                  <tr>\r\n                    <td>SORT BY : </td>\r\n                    <td>\r\n                      [insert_php] echo $input; [/insert_php]\r\n                      <select name=\"sortby\" onchange=\"this.form.submit();\">\r\n                        [insert_php] echo $select; [/insert_php]\r\n                        <option value=\"popularity\">Popularity</option>\r\n                        <option value=\"highprice\">Highest Price</option>\r\n                        <option value=\"lowprice\">Lowest Price</option>\r\n                        <option value=\"brands\">Brands</option>\r\n                      </select>\r\n                    </td>\r\n                  </tr>\r\n                </table>\r\n                </form>\r\n              </div>\r\n              </div>\r\n              <div class=\"item-listview column full-length\">\r\n                [insert_php]\r\n                  \r\n                  $query=$mysqli->query($sql);                  \r\n                  $total = mysqli_num_rows($query);\r\n                  $pages = ceil($total/$halaman);\r\n                  $sqlpage = $sql.\" LIMIT $mulai, $halaman\";\r\n                  $querypage = $mysqli->query($sqlpage);\r\n\r\n                  while ($hasil=$querypage->fetch_assoc()) {\r\n\r\n                        $brandby=\"SELECT * FROM wp_brand WHERE kdbrand=\'$hasil[kdbrand]\'\";\r\n                        $qbrandby=$mysqli->query($brandby);\r\n                        $resbrandby=$qbrandby->fetch_assoc();\r\n                        echo \'<div class=\"listview spccols-4 slideitem\">\';\r\n                        echo \'\r\n                          <div class=\"loading-itemlist\">\r\n                            <img src=\"https://rizkymalms.com/our11/wp-content/uploads/2018/08/loading1.gif\">\r\n                          </div>\r\n                        \';\r\n                        if($hasil[\'stock_product\'] == 0){\r\n                          echo \'<div class=\"listview-stampc\"><img src=\"https://rizkymalms.com/our11/wp-content/uploads/2018/08/stam-soldout.png\"></div>\';\r\n                        }\r\n                        echo \'<div class=\"slideitem